# add, subtract, multiply, divide, power, percentage, average

def add(a,b):
    return a+b
def subtract(a,b):
    return a-b
def multiply(a,b):
    return a*b
def divide(a,b):
    return a/b
def power(a,b):
    return a ** b
def percentage(part,total):
    return (part/total)*100
def average(numbers):
    return sum(numbers)/len(numbers)
def modulo(a,b):
    return a%b
def floordivision(a,b):
    return a//b
